import React, { useState, useEffect } from 'react';
import { toast } from 'react-hot-toast';
import { PlusIcon, SparklesIcon } from '@heroicons/react/24/outline';
import TaskItem from './TaskItem';
import TaskForm from './TaskForm';
import { getTasks, deleteTask, updateTask } from '../services/api';

function TaskList() {
  const [tasks, setTasks] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      // For now, let's use mock data since backend might not be ready
      // Remove this mock data once backend is working
      const mockTasks = [
        {
          id: 1,
          title: 'Welcome to TaskFlow',
          description: 'This is a sample task. The AI will help prioritize your tasks!',
          priority: 3,
          status: 'pending',
          deadline: new Date().toISOString(),
          ai_priority_score: 4,
          ai_suggested_deadline: new Date(Date.now() + 86400000).toISOString()
        }
      ];
      setTasks(mockTasks);
      setLoading(false);

      // Uncomment this when backend is ready
      // const data = await getTasks();
      // setTasks(data);
    } catch (error) {
      toast.error('Failed to fetch tasks');
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        setTasks(tasks.filter(task => task.id !== id));
        toast.success('Task deleted');
      } catch (error) {
        toast.error('Failed to delete task');
      }
    }
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      setTasks(tasks.map(t => t.id === id ? { ...t, status: newStatus } : t));
      toast.success('Task updated');
    } catch (error) {
      toast.error('Failed to update task');
    }
  };

  const getPriorityColor = (priority) => {
    const colors = {
      1: 'bg-gray-100 text-gray-800',
      2: 'bg-blue-100 text-blue-800',
      3: 'bg-green-100 text-green-800',
      4: 'bg-yellow-100 text-yellow-800',
      5: 'bg-red-100 text-red-800'
    };
    return colors[priority] || colors[1];
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8 max-w-4xl">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-900">TaskFlow</h1>
        <button
          onClick={() => setShowForm(true)}
          className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
        >
          <PlusIcon className="h-5 w-5 mr-2" />
          New Task
        </button>
      </div>

      {showForm && (
        <div className="mb-6">
          <TaskForm
            onTaskCreated={(newTask) => {
              setTasks([newTask, ...tasks]);
              setShowForm(false);
            }}
            onCancel={() => setShowForm(false)}
          />
        </div>
      )}

      <div className="bg-white shadow overflow-hidden sm:rounded-md">
        <ul className="divide-y divide-gray-200">
          {tasks.length === 0 ? (
            <li className="p-6 text-center text-gray-500">
              No tasks yet. Create your first task!
            </li>
          ) : (
            tasks.map(task => (
              <TaskItem
                key={task.id}
                task={task}
                onDelete={handleDelete}
                onStatusChange={handleStatusChange}
                getPriorityColor={getPriorityColor}
              />
            ))
          )}
        </ul>
      </div>

      <div className="mt-6 bg-blue-50 border border-blue-200 rounded-md p-4">
        <div className="flex items-center">
          <SparklesIcon className="h-5 w-5 text-blue-500 mr-2" />
          <span className="text-sm text-blue-700">
            AI is analyzing your tasks to suggest optimal priorities and deadlines
          </span>
        </div>
      </div>
    </div>
  );
}

export default TaskList;
