import React, { useState, useEffect } from 'react';

function App() {
  const [tasks, setTasks] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    priority: 3,
    deadline: ''
  });

  useEffect(() => {
    // Mock data
    setTimeout(() => {
      setTasks([
        {
          id: 1,
          title: 'Welcome to TaskFlow',
          description: 'This is a sample task. The AI will help prioritize your tasks!',
          priority: 3,
          status: 'pending',
          deadline: new Date().toISOString(),
          ai_priority_score: 4,
          ai_suggested_deadline: new Date(Date.now() + 86400000).toISOString()
        }
      ]);
      setLoading(false);
    }, 1000);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!newTask.title) return;

    const task = {
      id: Date.now(),
      ...newTask,
      status: 'pending'
    };
    setTasks([task, ...tasks]);
    setShowForm(false);
    setNewTask({ title: '', description: '', priority: 3, deadline: '' });
  };

 const getPriorityClass = (priority) => {
  return `priority-badge priority-${priority}`;
};

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="app">
      <div className="header">
        <h1>TaskFlow</h1>
        <button className="btn" onClick={() => setShowForm(!showForm)}>
          {showForm ? 'Cancel' : '+ New Task'}
        </button>
      </div>

      {showForm && (
        <div className="form-container">
          <h2 style={{ marginBottom: '20px' }}>Create New Task</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Title *</label>
              <input
                type="text"
                value={newTask.title}
                onChange={(e) => setNewTask({...newTask, title: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                rows="3"
                value={newTask.description}
                onChange={(e) => setNewTask({...newTask, description: e.target.value})}
              />
            </div>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px' }}>
              <div className="form-group">
                <label>Priority (1-5)</label>
                <input
                  type="number"
                  min="1"
                  max="5"
                  value={newTask.priority}
                  onChange={(e) => setNewTask({...newTask, priority: parseInt(e.target.value)})}
                />
              </div>
              <div className="form-group">
                <label>Deadline</label>
                <input
                  type="date"
                  value={newTask.deadline}
                  onChange={(e) => setNewTask({...newTask, deadline: e.target.value})}
                />
              </div>
            </div>
            <button type="submit" className="btn" style={{ width: '100%' }}>
              Create Task
            </button>
          </form>
        </div>
      )}

      <div className="task-list">
        {tasks.length === 0 ? (
          <div style={{ padding: '40px', textAlign: 'center', color: '#6b7280' }}>
            No tasks yet. Create your first task!
          </div>
        ) : (
          tasks.map(task => (
            <div key={task.id} className="task-item">
              <div className="task-header">
                <span className="task-title">{task.title}</span>
                <span className={getPriorityClass(task.priority)}>
                  P{task.priority}
                </span>
                {task.ai_priority_score && (
                  <span className="ai-badge">
                    AI: {task.ai_priority_score}
                  </span>
                )}
              </div>
              {task.description && (
                <p className="task-description">{task.description}</p>
              )}
              <div className="task-meta">
                <span> {task.deadline ? new Date(task.deadline).toLocaleDateString() : 'No deadline'}</span>
                {task.ai_suggested_deadline && (
                  <span> AI suggests: {new Date(task.ai_suggested_deadline).toLocaleDateString()}</span>
                )}
                <select 
                  className="status-select"
                  value={task.status}
                  onChange={(e) => {
                    const updatedTasks = tasks.map(t => 
                      t.id === task.id ? {...t, status: e.target.value} : t
                    );
                    setTasks(updatedTasks);
                  }}
                >
                  <option value="pending">Pending</option>
                  <option value="in_progress">In Progress</option>
                  <option value="completed">Completed</option>
                </select>
                <button 
                  className="delete-btn"
                  onClick={() => {
                    if (window.confirm('Delete this task?')) {
                      setTasks(tasks.filter(t => t.id !== task.id));
                    }
                  }}
                >

                </button>
              </div>
            </div>
          ))
        )}
      </div>

      <div className="ai-info">
         AI is analyzing your tasks to suggest optimal priorities and deadlines
      </div>
    </div>
  );
}

export default App;
